mapper.select:
SELECT orderId, receiver, userId, userAddr, userPostal, userAddrDetail, tel, totalPrice, orderDate, delivery
FROM ordertable
where userId=#{userId}

mapper.update:
UPDATE product
SET stock = stock - #{amount_detail}
WHERE productId=#{productId}

UPDATE product
SET buyCount = buyCount + #{amount_detail}
WHERE productId=#{productId}
